import React from "react";
import { Map as Maps, Marker } from "pigeon-maps";
import './Map.css';
import { connect } from "react-redux";
import { getPosition } from "../../store/actions"

class Map extends React.Component {
    constructor() {
        super();

        this.callGetPosition = this.callGetPosition.bind(this);
    }

    componentDidMount() {
      this.callGetPosition();
    }

    callGetPosition() {
      setInterval(getPosition, 500);  
    }

    render() {
        const { latitude, longetude } = this.props;
        return (
            <section className="Maps">
                <Maps height={600} defaultCenter={[50.879, 4.6997]} defaultZoom={4}>
                    <Marker width={50} anchor={[latitude, longetude]} />
                </Maps>
            </section>
        );
    }
}

const mapStateToProps = (state) => ({
    latitude: state.issPosition.latitude,
    longetude: state.issPosition.longitude,
})

export default connect(mapStateToProps)(Map)
